Bash(NODE_ENV=development node -e "
const { db } = require(''./server/core/database.js'');
const { bookings } = require(''./shared/schema.js'');

(async () => {
  try {
    const allBookings = await db.select().from(bookings);
    console.log(''Total bookings in database:'', allBookings.length);

    if (allBookings.length > 0) {
      console.log(''Sample booking IDs:'', allBookings.slice(0, 5).map(b => b.id));
    }
  } catch (error) {
    console.error(''Database query error:'', error);
  }
})();
")
