// EXTERNAL REVIEWER'S EXACT FIX: Create session middleware
export function createSessionMiddleware() {
  const PgSession = ConnectPgSimple(session);

  const sessionConfig = {
    store: new PgSession({
      conString: ENV.DATABASE_URL,
      tableName: 'sessions',
