// VERIFY SMS CODE
app.post('/api/auth/verify-phone', async (req, res) => {
  try {
    const { phoneNumber, verificationCode } = req.body;

    if (!phoneNumber || !verificationCode) {
      return res.status(400).json({ error: 'Phone number and code required' });
