import { storage } from '../server/core/storage';
import { uploadContractSigningPage } from '../server/core/cloud-storage';
import { db } from '../server/core/database';

async function regenerateAllSigningPages() {
  console.log('üîÑ Starting regeneration of all contract signing pages...');

  try {
    // Get all contracts that need signing pages (sent or draft status)
    const contracts = await db.query(`
      SELECT id, contract_number, status, user_id 
      FROM contracts 
      WHERE status IN ('sent', 'draft')
      ORDER BY created_at DESC
    `);

    console.log(`üìã Found ${contracts.rows.length} contracts to regenerate`);

    let successCount = 0;
    let errorCount = 0;

    for (const contractRow of contracts.rows) {
      try {
        console.log(`\nüîß Processing contract #${contractRow.id} (${contractRow.contract_number})`);

        // Get full contract details
        const contract = await storage.getContract(contractRow.id);
        if (!contract) {
          console.log(`‚ö†Ô∏è Contract #${contractRow.id} not found in storage`);
          errorCount++;
          continue;
        }

        // Get user settings
        const userSettings = await storage.getSettings(contract.userId);

        // Generate and upload new signing page
        const result = await uploadContractSigningPage(contract, userSettings);

        if (result.success) {
          // Update the contract with new signing page URL
          await db.query(
            `UPDATE contracts SET signing_page_url = $1 WHERE id = $2`,
            [result.url, contract.id]
          );

          console.log(`‚úÖ Regenerated signing page: ${result.url}`);
          successCount++;
        } else {
          console.log(`‚ùå Failed to regenerate: ${result.error}`);
          errorCount++;
        }

      } catch (error) {
        console.error(`‚ùå Error processing contract #${contractRow.id}:`, error);
        errorCount++;
      }
    }

    console.log('\nüìä Regeneration Complete:');
    console.log(`‚úÖ Success: ${successCount} contracts`);
    console.log(`‚ùå Errors: ${errorCount} contracts`);

  } catch (error) {
    console.error('‚ùå Fatal error during regeneration:', error);
    process.exit(1);
  }

  process.exit(0);
}

// Run the regeneration
regenerateAllSigningPages();

scripts/regenerate-widget-tokens.ts.content:
import jwt from 'jsonwebtoken';
import QRCode from 'qrcode';
import { storage } from '../server/core/storage';

async function regenerateWidgetTokens() {
  const emails = ['timfulker@gmail.com', 'timfulkermusic@gmail.com'];

  console.log('üîÑ Starting widget token regeneration...');

  for (const email of emails) {
    try {
      // Get user by email
      const user = await storage.getUserByEmail(email);
      if (!user) {
        console.log(`‚ùå User not found: ${email}`);
        continue;
      }

      // Generate new widget token
      const secret = process.env.JWT_SECRET || process.env.SESSION_SECRET;
      if (!secret) {
        console.log(`‚ùå No JWT secret configured for token generation`);
        continue;
      }

      const token = jwt.sign(
        { userId: user.id, type: 'widget' },
        secret,
        { expiresIn: '30d' }
      );

      const widgetUrl = `${process.env.APP_URL || 'https://www.musobuddy.com'}/widget?token=${encodeURIComponent(token)}`;

      // Generate QR code
      const qrCodeDataUrl = await QRCode.toDataURL(widgetUrl, {
        width: 400,
        margin: 2,
        color: {
          dark: '#000000',
          light: '#FFFFFF'
        }
      });

      console.log(`\n‚úÖ Regenerated widget token for ${email} (ID: ${user.id})`);
      console.log(`üì± Widget URL: ${widgetUrl}`);
      console.log(`üî≥ QR Code generated (Data URL length: ${qrCodeDataUrl.length})`);
      console.log('\n-------------------\n');

      // Save QR code to file for easy access
      const fs = await import('fs');
      const filename = `widget-qr-${user.id}.png`;
      const base64Data = qrCodeDataUrl.replace(/^data:image\/png;base64,/, '');
      fs.writeFileSync(filename, base64Data, 'base64');
      console.log(`üíæ QR code saved to: ${filename}`);

    } catch (error: any) {
      console.error(`‚ùå Failed to regenerate token for ${email}:`, error.message);
    }
  }

  console.log('\n‚úÖ Widget token regeneration complete!');
  process.exit(0);
}

// Run the script
regenerateWidgetTokens().catch(error => {
  console.error('‚ùå Script failed:', error);
  process.exit(1);
});

