        // "10th September" or "10th of September"
        /(\d{1,2})(?:st|nd|rd|th)?\s+(?:of\s+)?(\w+)(?!\s+\d{4})/gi,
        // Standard formats like "10/09/2025" or "10-09-2025"
        /(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{2,4})/g
