          console.log(`‚úÖ Successfully parsed date: ${data.eventDate}`);
        }
      } catch (e) {
        console.log(`‚ùå Date parsing failed for: "${dateStr}"`);
      }
      break;
    }
  }

  // Extract email if present in message
  const emailMatch = messageText.match(/[^\s@]+@[^\s@]+\.[^\s@]+/);
