import { z } from 'zod';
import type { Request, Response, NextFunction } from 'express';

// Validation middleware factory
export const validateBody = (schema: z.ZodSchema) => {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      const result = schema.safeParse(req.body);

      if (!result.success) {
        const errors = result.error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message
        }));

        return res.status(400).json({
          error: 'Validation failed',
          details: errors,
          timestamp: new Date().toISOString()
        });
      }

      // Replace req.body with validated data
      req.body = result.data;
      next();
    } catch (error) {
      console.error('Validation middleware error:', error);
      res.status(500).json({
        error: 'Internal validation error',
        timestamp: new Date().toISOString()
      });
    }
  };
};

// Query parameter validation
export const validateQuery = (schema: z.ZodSchema) => {
  return (req: Request, res: Response, next: NextFunction) => {
    try {
      const result = schema.safeParse(req.query);

      if (!result.success) {
        const errors = result.error.errors.map(err => ({
          field: err.path.join('.'),
          message: err.message
        }));

        return res.status(400).json({
          error: 'Query validation failed',
          details: errors,
          timestamp: new Date().toISOString()
        });
      }

      req.query = result.data;
      next();
    } catch (error) {
      console.error('Query validation middleware error:', error);
      res.status(500).json({
        error: 'Internal validation error',
        timestamp: new Date().toISOString()
      });
    }
  };
};

// Common validation schemas
export const schemas = {
  // Contract creation
  createContract: z.object({
    clientName: z.string().trim().min(2, 'Client name must be at least 2 characters').max(100, 'Client name too long'),
    clientEmail: z.string().email('Invalid email format').optional(),
    clientPhone: z.string().optional(),
    eventDate: z.string().regex(/^\d{4}-\d{2}-\d{2}$/, 'Date must be in YYYY-MM-DD format'),
    eventTime: z.string().optional(),
    fee: z.number().positive('Fee must be positive').optional(),
    venue: z.string().trim().min(1, 'Venue is required').max(200, 'Venue name too long'),
    enquiryId: z.number().int().positive().optional()
  }),

  // Contract signing
  signContract: z.object({
    clientSignature: z.string().trim().min(2, 'Signature must be at least 2 characters').max(100, 'Signature too long'),
    clientIP: z.string().optional(),
    clientPhone: z.string().optional(),
    clientAddress: z.string().optional(),
    venueAddress: z.string().optional()
  }),

  // Booking creation - extremely flexible validation, only basic checks
  createBooking: z.object({
    title: z.string().optional().nullable(),
    clientName: z.string().optional().nullable(), // Even client name can be empty for incomplete bookings
    clientEmail: z.string().email('Invalid email format').optional().nullable(),
    clientPhone: z.string().optional().nullable(),
    eventDate: z.string().optional().nullable(), // Date is optional to allow saving incomplete bookings
    eventTime: z.string().optional().nullable(),
    eventEndTime: z.string().optional().nullable(),
    venue: z.string().optional().nullable(),
    venueAddress: z.string().optional().nullable(),
    fee: z.union([z.string(), z.number()]).optional().nullable(), // Accept both string and number
    deposit: z.union([z.string(), z.number()]).optional().nullable(), // Accept both string and number
    status: z.string().optional().nullable(),
    notes: z.string().optional().nullable(),
    gigType: z.string().optional().nullable(),
    equipmentRequirements: z.string().optional().nullable(),
    specialRequirements: z.string().optional().nullable()
  }).refine((data) => {
    // At least some basic info should be provided
    return data.clientName || data.clientEmail || data.clientPhone || data.venue || data.eventDate;
  }, {
    message: "At least one field (client name, email, phone, venue, or date) must be provided"
  }),

  // Invoice creation
  createInvoice: z.object({
    enquiryId: z.number().int().positive('Invalid booking ID'),
    customItems: z.array(z.object({
      description: z.string().trim().min(1, 'Item description required').max(200, 'Description too long'),
      amount: z.number().positive('Amount must be positive')
    })).optional()
  }),

  // User settings update - COMPREHENSIVE SCHEMA TO PREVENT DATA LOSS
  updateSettings: z.object({
    // Business Details
    businessName: z.string().trim().min(1, 'Business name required').max(100, 'Business name too long').optional(),
    businessEmail: z.string().email('Invalid email format').optional(),
    businessAddress: z.string().max(500, 'Address too long').optional(),
    addressLine1: z.string().max(200, 'Address line too long').optional(),
    addressLine2: z.string().max(200, 'Address line too long').optional(),
    city: z.string().max(100, 'City name too long').optional(),
    county: z.string().max(100, 'County name too long').optional(),
    postcode: z.string().max(20, 'Postcode too long').optional(),
    phone: z.string().max(50, 'Phone number too long').optional(),
    website: z.string().url('Invalid website URL').optional().or(z.literal('')),
    taxNumber: z.string().max(50, 'Tax number too long').optional(),
    emailFromName: z.string().max(100, 'From name too long').optional(),

    // Financial Settings
    nextInvoiceNumber: z.number().int().min(1, 'Invoice number must be at least 1').optional(),
    defaultTerms: z.string().max(1000, 'Terms too long').optional(),
    bankDetails: z.string().max(1000, 'Bank details too long').optional(),
    defaultInvoiceDueDays: z.number().int().min(1, 'Due days must be at least 1').max(365, 'Due days too large').optional(),

    // AI Pricing
    aiPricingEnabled: z.boolean().optional(),
    baseHourlyRate: z.number().min(0, 'Rate cannot be negative').optional(),
    minimumBookingHours: z.number().min(0, 'Hours cannot be negative').optional(),
    additionalHourRate: z.number().min(0, 'Rate cannot be negative').optional(),
    djServiceRate: z.number().min(0, 'Rate cannot be negative').optional(),
    pricingNotes: z.string().max(1000, 'Notes too long').optional(),
    specialOffers: z.string().max(1000, 'Offers text too long').optional(),

    // Instruments and Performance
    primaryInstrument: z.string().max(100, 'Instrument name too long').optional(),
    secondaryInstruments: z.array(z.string().max(100, 'Instrument name too long')).optional(),
    customGigTypes: z.array(z.string().max(100, 'Gig type name too long')).optional(),
    bookingDisplayLimit: z.enum(['50', 'all']).optional(),

    // Theme Settings
    themeTemplate: z.string().max(50, 'Template name too long').optional(),
    themeTone: z.string().max(50, 'Tone name too long').optional(),
    themeFont: z.string().max(50, 'Font name too long').optional(),
    themeAccentColor: z.string().regex(/^#[0-9A-Fa-f]{6}$/, 'Invalid color format').optional(),
    themeLogoUrl: z.string().url('Invalid logo URL').optional().or(z.literal('')),
    themeSignatureUrl: z.string().url('Invalid signature URL').optional().or(z.literal('')),
    themeBanner: z.string().max(200, 'Banner text too long').optional(),
    themeShowSetlist: z.boolean().optional(),
    themeShowRiderNotes: z.boolean().optional(),
    themeShowQrCode: z.boolean().optional(),
    themeShowTerms: z.boolean().optional(),
    themeCustomTitle: z.string().max(100, 'Custom title too long').optional(),
  }),

  // AI template generation
  generateTemplate: z.object({
    enquiryText: z.string().trim().min(10, 'Enquiry text too short').max(5000, 'Enquiry text too long'),
    templateType: z.enum(['quote', 'contract', 'email'], { invalid_type_error: 'Invalid template type' })
  }),

  // Pagination
  pagination: z.object({
    page: z.string().regex(/^\d+$/, 'Page must be a number').transform(Number).refine(n => n > 0, 'Page must be positive').optional(),
    limit: z.string().regex(/^\d+$/, 'Limit must be a number').transform(Number).refine(n => n > 0 && n <= 100, 'Limit must be 1-100').optional()
  })
};

// Input sanitization middleware
export const sanitizeInput = (req: Request, res: Response, next: NextFunction) => {
  const sanitizeValue = (value: any): any => {
    if (typeof value === 'string') {
      // SECURITY FIX: Enhanced XSS protection - simplified for server-side compatibility
      try {
        // Basic HTML tag and script removal - server-safe implementation
        return value
          .replace(/<script[^>]*>.*?<\/script>/gis, '')
          .replace(/<[^>]*>/g, '')
          .replace(/javascript:/gi, '')
          .replace(/on\w+\s*=/gi, '')
          .trim();
      } catch (error) {
        // Fallback to basic sanitization if DOMPurify fails
        console.warn('Input sanitization fallback used');
        return value.replace(/<[^>]*>/g, '').trim();
      }
    }
    if (Array.isArray(value)) {
      return value.map(sanitizeValue);
    }
    if (typeof value === 'object' && value !== null) {
      const sanitized: any = {};
      for (const [key, val] of Object.entries(value)) {
        sanitized[key] = sanitizeValue(val);
      }
      return sanitized;
    }
    return value;
  };

  if (req.body) {
    req.body = sanitizeValue(req.body);
  }

  if (req.query) {
    req.query = sanitizeValue(req.query);
  }

  next();
};

server/routes/admin-database-routes.ts.content:
import type { Express, Request, Response } from 'express';
import { db } from '../core/database.js';
import { eq, sql, like, or } from 'drizzle-orm';
import { 
  bookings, 
  clients, 
  contracts, 
  invoices, 
  userSettings, 
  complianceDocuments,
  bookingConflicts,
  conflictResolutions,
  unparseableMessages,
  emailTemplates,
  globalGigTypes
} from '../../shared/schema.js';
import { requireAuth, requireAdmin } from '../middleware/auth.js';

// Define available tables and their schemas
const AVAILABLE_TABLES = {
  'bookings': bookings,
  'clients': clients,
  'contracts': contracts,
  'invoices': invoices,
  'userSettings': userSettings,
  'complianceDocuments': complianceDocuments,
  'bookingConflicts': bookingConflicts,
  'conflictResolutions': conflictResolutions,
  'unparseableMessages': unparseableMessages,
  'emailTemplates': emailTemplates,
  'globalGigTypes': globalGigTypes
} as const;

// Map export names to actual database table names (for tables with naming mismatches)
const TABLE_NAME_MAPPING: Record<string, string> = {
  'complianceDocuments': 'compliance_documents',
  'userSettings': 'user_settings',
  'bookingConflicts': 'booking_conflicts',
  'conflictResolutions': 'conflict_resolutions',
  'unparseableMessages': 'unparseable_messages',
  'emailTemplates': 'email_templates',
  'globalGigTypes': 'global_gig_types'
};

type TableName = keyof typeof AVAILABLE_TABLES;

export function setupAdminDatabaseRoutes(app: Express) {
  // Get list of available tables with metadata
  app.get('/api/admin/database/tables', requireAuth, requireAdmin, async (req: Request, res: Response) => {
    try {
      const tables = [];

      for (const [tableName, tableSchema] of Object.entries(AVAILABLE_TABLES)) {
        try {
          // Get row count for each table
          const countResult = await db.select({ count: sql<number>`count(*)` }).from(tableSchema);
          const rowCount = countResult[0]?.count || 0;

          // Get column names using SQL query instead of schema metadata
          const actualTableName = TABLE_NAME_MAPPING[tableName] || tableName;
          const columnQuery = await db.execute(sql`
            SELECT column_name 
            FROM information_schema.columns 
            WHERE table_name = ${actualTableName} 
            AND table_schema = 'public'
            ORDER BY ordinal_position
          `);

          const columns = columnQuery.rows.map((row: any) => row.column_name);

          tables.push({
            name: tableName,
            rowCount,
            columns
          });
        } catch (error) {
          console.error(`Error getting metadata for table ${tableName}:`, error);
          // Add table with basic info even if column detection fails
          tables.push({
            name: tableName,
            rowCount: 0,
            columns: []
          });
        }
      }

      res.json(tables);
    } catch (error) {
      console.error('Error fetching database tables:', error);
      res.status(500).json({ error: 'Failed to fetch database tables' });
    }
  });

  // Get table data with pagination, search, and filtering
  app.get('/api/admin/database/data', requireAuth, requireAdmin, async (req: Request, res: Response) => {
    try {
      const { table, page = '1', limit = '50', search, filterColumn } = req.query;
      console.log(`üìä Backend: Request received for table: ${table}, page: ${page}, search: ${search}`);

      if (!table || typeof table !== 'string' || !(table in AVAILABLE_TABLES)) {
        return res.status(400).json({ error: 'Invalid table name' });
      }

      const tableName = table as TableName;
      const tableSchema = AVAILABLE_TABLES[tableName];
      const pageNum = parseInt(page as string, 10);
      const limitNum = parseInt(limit as string, 10);
      const offset = (pageNum - 1) * limitNum;

      let query = db.select().from(tableSchema);

      // Order results to show meaningful data first (applied after search filtering)
      const shouldOrderMeaningfulFirst = (tableName === 'contracts' || tableName === 'invoices');

      // Apply search filtering using raw SQL for better compatibility
      if (search && typeof search === 'string' && search.trim()) {
        const searchTerm = search.trim();
        console.log(`üîç Searching ${tableName} table for: "${searchTerm}"`);

        // Use raw SQL to search across multiple text columns
        // This is more reliable than trying to access schema metadata
        if (tableName === 'bookings') {
          query = query.where(sql`
            LOWER(COALESCE(client_name, '')) LIKE LOWER(${`%${searchTerm}%`}) OR
            LOWER(COALESCE(event_type, '')) LIKE LOWER(${`%${searchTerm}%`}) OR
            LOWER(COALESCE(venue, '')) LIKE LOWER(${`%${searchTerm}%`}) OR
