import { db } from "../core/database";
import { contracts } from "../../shared/schema";
import { eq, and, desc, sql, or } from "drizzle-orm";

export class ContractStorage {
  private db = db;

  // ===== CONTRACT METHODS =====

  async getContract(id: number) {
