import { S3Client, PutObjectCommand } from '@aws-sdk/client-s3';
import { generateHybridWidgetHTML } from './hybrid-widget-generator';

const s3Client = new S3Client({
  region: 'auto',
  endpoint: `https://${process.env.R2_ACCOUNT_ID}.r2.cloudflarestorage.com`,
  credentials: {
    accessKeyId: process.env.R2_ACCESS_KEY_ID || '',
    secretAccessKey: process.env.R2_SECRET_ACCESS_KEY || ''
  }
});

export async function uploadWidgetToR2(userId: string, token: string): Promise<{
  success: boolean;
  url?: string;
  qrCodeUrl?: string;
  key?: string;
  error?: string;
}> {
  try {
    // Verify R2 environment variables
    const requiredEnvVars = ['R2_ACCOUNT_ID', 'R2_ACCESS_KEY_ID', 'R2_SECRET_ACCESS_KEY', 'R2_BUCKET_NAME'];
    const missingVars = requiredEnvVars.filter(varName => !process.env[varName]);

    if (missingVars.length > 0) {
      const error = `Missing R2 environment variables: ${missingVars.join(', ')}`;
      console.error('‚ùå', error);
      return {
        success: false,
        error
      };
    }

    console.log(`üîß Generating widget HTML for user ${userId} with token ${token}`);
    console.log(`üîß R2 Config: account=${process.env.R2_ACCOUNT_ID}, bucket=${process.env.R2_BUCKET_NAME}`);

    const widgetHTML = await generateHybridWidgetHTML(userId, token);
    const widgetKey = `widgets/widget-${userId}-${token}.html`;

    // Upload widget HTML
    const widgetCommand = new PutObjectCommand({
      Bucket: process.env.R2_BUCKET_NAME,
      Key: widgetKey,
      Body: widgetHTML,
      ContentType: 'text/html',
      CacheControl: 'public, max-age=3600'
    });

    await s3Client.send(widgetCommand);

    const widgetUrl = `https://pub-446248abf8164fb99bee2fc3dc3c513c.r2.dev/${widgetKey}`;

    // Generate and upload QR code
    const qrcode = await import('qrcode');
    const qrCodeBuffer = await qrcode.default.toBuffer(widgetUrl, {
      errorCorrectionLevel: 'M',
      type: 'png',
      margin: 1,
      color: {
        dark: '#000000',
        light: '#FFFFFF'
      },
      width: 256
    });

    const qrCodeKey = `widgets/qr-${userId}-${token}.png`;
    const qrCodeCommand = new PutObjectCommand({
      Bucket: process.env.R2_BUCKET_NAME,
      Key: qrCodeKey,
      Body: qrCodeBuffer,
      ContentType: 'image/png',
      CacheControl: 'public, max-age=86400' // Cache for 24 hours
    });

    await s3Client.send(qrCodeCommand);

    const qrCodeUrl = `https://pub-446248abf8164fb99bee2fc3dc3c513c.r2.dev/${qrCodeKey}`;

    console.log(`‚úÖ Widget uploaded to R2: ${widgetUrl}`);
    console.log(`‚úÖ QR Code uploaded to R2: ${qrCodeUrl}`);

    return {
      success: true,
      url: widgetUrl,
      qrCodeUrl: qrCodeUrl,
      key: widgetKey
    };

  } catch (error: any) {
    console.error('‚ùå Failed to upload widget to R2:', error);

    // Provide more specific error messages
    let errorMessage = error.message || 'Unknown error occurred';

    if (error.code === 'CredentialsError' || error.message?.includes('credentials')) {
      errorMessage = 'R2 credentials are invalid or missing';
    } else if (error.code === 'NetworkingError' || error.message?.includes('network')) {
      errorMessage = 'Network error connecting to R2 storage';
    } else if (error.message?.includes('Bucket')) {
      errorMessage = 'R2 bucket configuration error';
    }

    return {
      success: false,
      error: errorMessage
    };
  }
}

client/src/components/AddressAutocomplet.content:
import { useRef, useState, useEffect } from "react";
import { apiRequest } from "@/lib/queryClient";

interface AddressData {
  address: string;
  formattedAddress?: string;
  lat: number;
  lng: number;
  placeId?: string;
  contactInfo?: {
    phoneNumber: string;
    website: string;
  };
  businessInfo?: {
    openingHours: string[];
    rating: number | null;
    ratingCount: number;
    description: string;
  };
}

interface AddressAutocompleteProps {
  onSelect: (data: AddressData) => void;
  placeholder?: string;
  defaultValue?: string;
  value?: string; // Add controlled value prop
  className?: string;
  searchOnTabOnly?: boolean; // New prop to control search behavior
}

export default function AddressAutocomplete({
  onSelect,
  placeholder = "Start typing venue name or address...",
  defaultValue = "",
  value, // Add value prop
  className = "border rounded px-3 py-2 w-full min-w-[300px]",
  searchOnTabOnly = false // New prop to control search behavior
}: AddressAutocompleteProps) {
  const inputRef = useRef<HTMLInputElement>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [suggestions, setSuggestions] = useState<any[]>([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [inputValue, setInputValue] = useState(value || defaultValue);

  // Sync with external value changes (for controlled input)
  useEffect(() => {
    if (value !== undefined && value !== inputValue) {
      setInputValue(value);
    }
  }, [value]);

  // Fetch detailed place information
  const fetchPlaceDetails = async (placeId: string) => {
    try {
      console.log("üìç Fetching place details for:", placeId);
      const response = await apiRequest('/api/maps/place-details', {
        method: 'POST',
        body: JSON.stringify({ placeId }),
      });

      const placeDetails = await response.json();
      console.log("‚úÖ Place details retrieved:", placeDetails);
      return placeDetails;
    } catch (error) {
      console.warn("‚ùå Failed to fetch place details:", error);
      return null;
    }
  };

  // Search for places using our backend API (which can use Places API New)
  const searchPlaces = async (query: string) => {
    if (!query.trim() || query.length < 3) { // Require at least 3 characters
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      console.log("üó∫Ô∏è Searching places for:", query);
      const response = await apiRequest('/api/maps/places-search', {
        method: 'POST',
        body: JSON.stringify({ query }),
      });

      const data = await response.json();

      if (data.suggestions) {
        setSuggestions(data.suggestions);
        setShowSuggestions(true);
      }
    } catch (error) {
      console.error("‚ùå Places search error:", error);
      setError("Failed to search places");
      setSuggestions([]);
    } finally {
      setIsLoading(false);
    }
  };

  // Debounce search requests - only run if not searchOnTabOnly mode
  useEffect(() => {
    // Skip automatic search if searchOnTabOnly is enabled
    if (searchOnTabOnly) {
      return;
    }

    const timeoutId = setTimeout(() => {
      if (inputValue && inputValue.length >= 3) { // Only search after 3+ characters
        searchPlaces(inputValue);
      }
    }, 800); // Longer delay to allow more typing

    return () => clearTimeout(timeoutId);
  }, [inputValue, searchOnTabOnly]);

  const handleSelectSuggestion = async (suggestion: any) => {
    console.log("üîç handleSelectSuggestion called");
    // Immediately hide suggestions and clear them to prevent UI issues
    setShowSuggestions(false);
    setSuggestions([]);
    setError(null);

    // Start with basic address data
    const baseAddressData: AddressData = {
      address: suggestion.name || suggestion.formatted_address,
      formattedAddress: suggestion.formatted_address,
      lat: suggestion.lat,
      lng: suggestion.lng,
      placeId: suggestion.placeId
    };

    console.log("üìç Selected place data:", baseAddressData);

    // Set loading state while fetching details
    setIsLoading(true);
    setInputValue(suggestion.name || suggestion.formatted_address);

    // Fetch detailed place information if we have a place ID
    if (suggestion.placeId) {
      try {
        const placeDetails = await fetchPlaceDetails(suggestion.placeId);
        if (placeDetails) {
          const enrichedAddressData: AddressData = {
            ...baseAddressData,
            contactInfo: placeDetails.contactInfo,
            businessInfo: placeDetails.businessInfo
          };
          console.log("üìç Enriched address data with place details:", enrichedAddressData);
          onSelect(enrichedAddressData);
        } else {
          // Fallback to basic data if details fetch failed
          onSelect(baseAddressData);
        }
      } catch (error) {
        console.warn("Failed to fetch place details, using basic data:", error);
        onSelect(baseAddressData);
      }
    } else {
      onSelect(baseAddressData);
    }

    setIsLoading(false);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const value = e.target.value;
    console.log("üîç Input changed to:", value);
    setInputValue(value);

    if (!value.trim()) {
      setSuggestions([]);
      setShowSuggestions(false);
    }
  };

  // Handle Tab key press to trigger search
  const handleKeyDown = async (e: React.KeyboardEvent<HTMLInputElement>) => {
    if (e.key === 'Tab' && inputValue.trim() && inputValue.length >= 3) {
      e.preventDefault(); // Prevent default tab behavior
      console.log("‚å®Ô∏è Tab pressed, searching for:", inputValue);
      await searchPlaces(inputValue);
    }
  };

  // Fallback geocoding when user types and blurs without selecting
  const handleBlur = async () => {
    // Hide suggestions immediately on blur
    setTimeout(() => {
      setShowSuggestions(false);
      setSuggestions([]);
    }, 150); // Small delay to allow click events to complete
  };

  return (
    <div className="relative">
      <input
        ref={inputRef}
        type="text"
        placeholder={placeholder}
        value={inputValue}
        onChange={handleInputChange}
        onKeyDown={handleKeyDown}
        onBlur={handleBlur}
        className={`${className} ${isLoading ? 'opacity-50' : ''}`}
        disabled={isLoading}
      />

      {isLoading && (
        <div className="absolute right-3 top-1/2 transform -translate-y-1/2">
          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-primary"></div>
        </div>
      )}

      {showSuggestions && suggestions.length > 0 && (
        <div className="absolute z-50 w-full mt-1 bg-white dark:bg-gray-800 border border-gray-200 dark:border-gray-700 rounded-md shadow-lg max-h-60 overflow-y-auto">
          {suggestions.map((suggestion, index) => (
            <div
              key={index}
              className="px-4 py-2 hover:bg-gray-100 dark:hover:bg-gray-700 cursor-pointer border-b border-gray-100 dark:border-gray-700 last:border-b-0"
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                console.log("üîç Suggestion clicked, hiding suggestions");
                handleSelectSuggestion(suggestion);
              }}
            >
              <div className="font-medium text-sm">
                {suggestion.name}
              </div>
              <div className="text-xs text-gray-500 dark:text-gray-400">
                {suggestion.formatted_address}
              </div>
            </div>
          ))}
        </div>
      )}

      {error && (
        <div className="text-red-500 text-xs mt-1">
          {error}
        </div>
      )}
    </div>
  );
}

