import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Bell, MessageCircle, Trash2, Check, Eye } from "lucide-react";
import { Card } from "./ui/card";
import { Button } from "./ui/button";
import { Badge } from "./ui/badge";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface MessageNotification {
  id: number;
  userId: string;
  bookingId: number;
  senderEmail: string;
  subject: string;
  messageUrl: string;
  isRead: boolean;
  createdAt: string;
}

interface MessageNotificationsProps {
  userId: string;
}

export function MessageNotifications({ userId }: MessageNotificationsProps) {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [selectedNotification, setSelectedNotification] = useState<MessageNotification | null>(null);

  // Fetch message notifications
  const { data: notificationsData, isLoading, error } = useQuery({
    queryKey: ['notifications', 'messages', userId],
    queryFn: () => apiRequest(`/api/notifications/messages`),
  });

  // Ensure notifications is always an array
  const notifications: MessageNotification[] = Array.isArray(notificationsData) ? notificationsData : [];

  // Mark as read mutation
  const markAsReadMutation = useMutation({
    mutationFn: (id: number) => apiRequest(`/api/notifications/messages/${id}/read`, {
      method: 'PATCH'
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['notifications', 'messages'] });
      toast({
        title: "Message marked as read",
        description: "The notification has been marked as read.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error",
        description: "Failed to mark message as read.",
        variant: "destructive",
      });
    },
  });

  // Delete notification mutation  
  const deleteNotificationMutation = useMutation({
    mutationFn: (id: number) => apiRequest(`/api/notifications/messages/${id}`, {
      method: 'DELETE'
    }),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['notifications', 'messages'] });
      toast({
        title: "Notification deleted",
        description: "The notification has been removed.",
      });
    },
    onError: (error: any) => {
      toast({
        title: "Error", 
        description: "Failed to delete notification.",
        variant: "destructive",
      });
    },
  });

  const handleViewMessage = async (notification: MessageNotification) => {
    // Mark as read if not already read
    if (!notification.isRead) {
      markAsReadMutation.mutate(notification.id);
    }

    // Open the message in a new tab (stored in cloud storage)
    const messageUrl = `https://pub-446248abf8164fb99bee2fc3dc3c513c.r2.dev/${notification.messageUrl}`;
    window.open(messageUrl, '_blank');
  };

  const unreadCount = notifications.filter((n) => !n.isRead).length;

  if (isLoading) {
    return (
      <Card className="p-6">
        <div className="flex items-center gap-2 mb-4">
          <MessageCircle className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Client Messages</h3>
        </div>
        <p className="text-muted-foreground">Loading messages...</p>
      </Card>
    );
  }

  return (
    <Card className="p-6">
      <div className="flex items-center justify-between mb-4">
        <div className="flex items-center gap-2">
          <MessageCircle className="h-5 w-5" />
          <h3 className="text-lg font-semibold">Client Messages</h3>
          {unreadCount > 0 && (
            <Badge variant="destructive" className="h-5 text-xs">
              {unreadCount}
            </Badge>
          )}
        </div>
        {unreadCount > 0 && (
          <Badge variant="outline" className="text-xs">
            {unreadCount} unread
          </Badge>
        )}
      </div>

      {notifications.length === 0 ? (
        <div className="text-center py-8 text-muted-foreground">
          <MessageCircle className="h-12 w-12 mx-auto mb-3 opacity-50" />
          <p>No messages received yet</p>
          <p className="text-sm">Client replies to your emails will appear here</p>
        </div>
      ) : (
        <div className="space-y-3">
          {notifications.map((notification) => (
            <div
              key={notification.id}
              className={`p-4 border rounded-lg ${
                notification.isRead 
                  ? 'bg-background border-border' 
                  : 'bg-blue-50 border-blue-200 dark:bg-blue-950 dark:border-blue-800'
              }`}
            >
              <div className="flex items-start justify-between">
                <div className="flex-1 min-w-0">
                  <div className="flex items-center gap-2 mb-1">
                    <p className="font-medium text-sm truncate">
                      {notification.subject}
                    </p>
                    {!notification.isRead && (
                      <Badge variant="default" className="h-4 text-xs">New</Badge>
                    )}
                  </div>
                  <p className="text-sm text-muted-foreground mb-1">
                    From: {notification.senderEmail}
                  </p>
                  <p className="text-xs text-muted-foreground">
                    Booking #{notification.bookingId} ‚Ä¢ {new Date(notification.createdAt).toLocaleDateString()}
                  </p>
                </div>

                <div className="flex items-center gap-1 ml-4">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handleViewMessage(notification)}
                    className="h-8 px-2"
                  >
                    <Eye className="h-3 w-3" />
                  </Button>

                  {!notification.isRead && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => markAsReadMutation.mutate(notification.id)}
                      disabled={markAsReadMutation.isPending}
                      className="h-8 px-2"
                    >
                      <Check className="h-3 w-3" />
                    </Button>
                  )}

                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => deleteNotificationMutation.mutate(notification.id)}
                    disabled={deleteNotificationMutation.isPending}
                    className="h-8 px-2 text-destructive hover:text-destructive"
                  >
                    <Trash2 className="h-3 w-3" />
                  </Button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </Card>
  );
}

client/src/components/MusoBuddyLogo.tsx.content:
import { useTheme } from '@/hooks/useTheme';

interface MusoBuddyLogoProps {
  size?: 'small' | 'medium' | 'large';
  showTagline?: boolean;
  iconOnly?: boolean;
  className?: string;
}

export function MusoBuddyLogo({ 
  size = 'medium', 
  showTagline = true, 
  iconOnly = false,
  className = '' 
}: MusoBuddyLogoProps) {
  const { currentTheme } = useTheme();

  // Use consistent midnight blue for MusoBuddy logo (brand consistency)
  const colors = {
    primary: '#191970', // Midnight blue - consistent branding
    secondary: '#1e3a8a' // Darker blue
  };

  // Size configurations
  const sizeConfig = {
    small: {
      icon: 40,
      iconRadius: 8,
      metronomeBody: { width: 14, height: 22 },
      metronomeArm: { width: 1.5, height: 14, top: 4 },
      text: 18,
      tagline: 10,
      gap: 10
    },
    medium: {
      icon: 60,
      iconRadius: 12,
      metronomeBody: { width: 20, height: 32 },
      metronomeArm: { width: 2, height: 20, top: 6 },
      text: 24,
      tagline: 12,
      gap: 15
    },
    large: {
      icon: 80,
      iconRadius: 20,
      metronomeBody: { width: 28, height: 45 },
      metronomeArm: { width: 3, height: 28, top: 8 },
      text: 36,
      tagline: 14,
      gap: 20
    }
  };

  const config = sizeConfig[size];

  // Animation keyframes
  const tickAnimation = `
    @keyframes tick {
      0% { transform: translateX(-50%) rotate(-18deg); }
      100% { transform: translateX(-50%) rotate(18deg); }
    }
  `;

  const MetronomeIcon = () => (
    <div
      style={{
        width: config.icon,
        height: config.icon,
        background: colors.primary,
        borderRadius: config.iconRadius,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        position: 'relative',
        boxShadow: `0 ${config.icon * 0.1}px ${config.icon * 0.3}px ${colors.primary}40`,
        flexShrink: 0
      }}
    >
      {/* Metronome Body */}
      <div
        style={{
          width: config.metronomeBody.width,
          height: config.metronomeBody.height,
          background: 'white',
          clipPath: 'polygon(25% 0%, 75% 0%, 100% 100%, 0% 100%)',
          position: 'relative'
        }}
      >
        {/* Metronome Arm */}
        <div
          style={{
            position: 'absolute',
            top: config.metronomeArm.top,
            left: '50%',
            transform: 'translateX(-50%)',
            width: config.metronomeArm.width,
            height: config.metronomeArm.height,
            background: colors.primary,
            transformOrigin: 'bottom',
            animation: 'tick 1.2s ease-in-out infinite alternate',
            borderRadius: 1
          }}
        />
      </div>
      <style dangerouslySetInnerHTML={{ __html: tickAnimation }} />
    </div>
  );

  if (iconOnly) {
    return (
      <div className={className}>
        <MetronomeIcon />
      </div>
    );
  }

  return (
    <div 
      className={className}
      style={{
        display: 'inline-flex',
        alignItems: 'center',
        gap: config.gap
      }}
    >
      <MetronomeIcon />

      <div style={{ textAlign: 'left' }}>
        <div
          style={{
            fontSize: config.text,
            fontWeight: 700,
            color: colors.primary,
            letterSpacing: -1,
            lineHeight: 1,
            marginBottom: showTagline ? 4 : 0
          }}
        >
          MusoBuddy
        </div>

        {showTagline && (
          <div
            style={{
              fontSize: config.tagline,
              color: '#64748b',
              fontWeight: 500,
              fontStyle: 'italic'
            }}
          >
            Less admin, more music
          </div>
        )}
      </div>
    </div>
  );
}

