import { useState } from "react";
import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { MoreHorizontal, MessageSquare, FileText, DollarSign, ThumbsUp, XCircle, Shield, Upload, History } from "lucide-react";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";

interface BookingActionMenuProps {
  booking: any;
  onEditBooking?: (booking: any) => void;
  onSendCompliance?: (booking: any) => void;
  onManageDocuments?: (booking: any) => void;
  onViewCommunications?: (booking: any) => void;
}

export default function BookingActionMenu({ booking, onEditBooking, onSendCompliance, onManageDocuments, onViewCommunications }: BookingActionMenuProps) {
  const { toast } = useToast();
  const [, navigate] = useLocation();
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const statusUpdateMutation = useMutation({
    mutationFn: async ({ bookingId, newStatus }: { bookingId: number; newStatus: string }) => {
      return apiRequest(`/api/bookings/${bookingId}`, {
        method: 'PATCH',
        body: JSON.stringify({ status: newStatus }),
        headers: { 'Content-Type': 'application/json' }
      });
    },
    onSuccess: (_, { newStatus }) => {
      queryClient.invalidateQueries({ queryKey: ["/api/bookings"] });
      toast({
        title: "Success",
        description: `Booking status updated to ${newStatus.replace('_', ' ')}`,
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update booking status",
        variant: "destructive",
      });
    },
  });

  const handleAction = (action: string) => {
    // Close dropdown when action is taken
    setDropdownOpen(false);

    let newStatus = booking.status; // Default to current status
    let message = "";

    switch (action) {
      case 'respond_to_client':
        // Auto-update status from 'new' to 'awaiting_response' (in progress)
        if (booking.status === 'new') {
          newStatus = 'awaiting_response';
        }
        // Navigate to templates page with booking context
        navigate(`/templates?bookingId=${booking.id}&action=respond`);
        break;
      case 'issue_contract':
        // Navigate to contracts page with booking data pre-filled
        // NOTE: Status will only change to 'contract_sent' when contract is actually sent via email
        navigate(`/contracts?bookingId=${booking.id}&action=create`);
        break;
      case 'issue_invoice':
        // Navigate to invoices page with booking data pre-filled
        navigate(`/invoices?bookingId=${booking.id}&action=create`);
        break;
      case 'send_thankyou':
        // Navigate to templates page with booking context for thank you message
        navigate(`/templates?bookingId=${booking.id}&action=thankyou`);
        break;
      case 'edit_booking':
        // Open booking details dialog for editing
        if (onEditBooking) {
          onEditBooking(booking);
        }
        return;
      case 'send_compliance':
        // Open compliance dialog directly on bookings page
        if (onSendCompliance) {
          onSendCompliance(booking);
        } else {
          // Fallback to navigation if no callback provided
          navigate(`/compliance?bookingId=${booking.id}&action=send`);
        }
        return;
      case 'manage_documents':
        // Open documents manager dialog
        if (onManageDocuments) {
          onManageDocuments(booking);
        }
        return;
      case 'view_communications':
        // Open communication history dialog
        if (onViewCommunications) {
          onViewCommunications(booking);
        }
        return;
      case 'reject':
        newStatus = 'rejected';
        message = "Booking rejected";
        break;
    }

    // Update status if it changed, then provide user feedback
    if (newStatus !== booking.status) {
      statusUpdateMutation.mutate({ 
        bookingId: booking.id, 
        newStatus 
      });
    } else if (message) {
      toast({
        title: "Action Completed", 
        description: message,
      });
    }
  };

  return (
    <DropdownMenu open={dropdownOpen} onOpenChange={setDropdownOpen}>
      <DropdownMenuTrigger asChild>
        <Button 
          variant="outline" 
          size="sm"
          onClick={(e) => {
            e.stopPropagation();
            setDropdownOpen(!dropdownOpen);
          }}
        >
          <MoreHorizontal className="w-4 h-4 mr-1" />
          Respond
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent 
        align="end" 
        className="w-48 z-[9999999]" 
        side="bottom"
        sideOffset={5}
        onPointerDownOutside={() => setDropdownOpen(false)}
        onEscapeKeyDown={() => setDropdownOpen(false)}
      >
        <DropdownMenuItem 
          onClick={() => handleAction('respond_to_client')}
          disabled={statusUpdateMutation.isPending}
        >
          <MessageSquare className="w-4 h-4 mr-2" />
          Respond to Client
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => handleAction('issue_contract')}
          disabled={statusUpdateMutation.isPending}
        >
          <FileText className="w-4 h-4 mr-2" />
          Issue Contract
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => handleAction('issue_invoice')}
          disabled={statusUpdateMutation.isPending}
        >
          <DollarSign className="w-4 h-4 mr-2" />
          Issue Invoice
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => handleAction('send_thankyou')}
          disabled={statusUpdateMutation.isPending}
        >
          <ThumbsUp className="w-4 h-4 mr-2" />
          Send Thank You
        </DropdownMenuItem>
        {onEditBooking && (
          <DropdownMenuItem 
            onClick={() => handleAction('edit_booking')}
            disabled={statusUpdateMutation.isPending}
          >
            <FileText className="w-4 h-4 mr-2" />
            Edit Booking
          </DropdownMenuItem>
        )}
        <DropdownMenuItem 
          onClick={() => handleAction('send_compliance')}
          disabled={statusUpdateMutation.isPending}
        >
          <Shield className="w-4 h-4 mr-2" />
          Send Compliance Documents
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => handleAction('manage_documents')}
          disabled={statusUpdateMutation.isPending}
        >
          <Upload className="w-4 h-4 mr-2" />
          Manage Documents
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => handleAction('view_communications')}
          disabled={statusUpdateMutation.isPending}
        >
          <History className="w-4 h-4 mr-2" />
          Communication History
        </DropdownMenuItem>
        <DropdownMenuItem 
          onClick={() => handleAction('reject')}
          disabled={statusUpdateMutation.isPending}
          className="text-red-600 focus:text-red-600"
        >
          <XCircle className="w-4 h-4 mr-2" />
          Reject
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
}

client/src/components/booking-actions-di.content:
import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { Textarea } from "@/components/ui/textarea";
import { ScrollArea } from "@/components/ui/scroll-area";
import { 
  MessageCircle, 
  Edit, 
  Eye, 
  FileText, 
  Receipt, 
  Send,
  Calendar,
  MapPin,
  Phone,
  Mail,
  Clock,
  Reply
} from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLocation } from "wouter";
import { format } from "date-fns";

interface BookingActionsDialogProps {
  booking: any;
  isOpen: boolean;
  onClose: () => void;
}

interface Communication {
  id: number;
  communicationType: string;
  direction: 'inbound' | 'outbound';
  subject: string | null;
  messageBody: string;
  sentAt: string;
  templateName: string | null;
  deliveryStatus: string;
}

export function BookingActionsDialog({ booking, isOpen, onClose }: BookingActionsDialogProps) {
  const [activeView, setActiveView] = useState<'actions' | 'communications' | 'reply'>('actions');
  const [replyMessage, setReplyMessage] = useState('');
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [, navigate] = useLocation();

  // Fetch communication history for this booking
  const { data: communications = [], isLoading: communicationsLoading } = useQuery({
    queryKey: ['communications', 'booking', booking?.id],
    queryFn: async () => {
      if (!booking?.id) return [];
      const response = await apiRequest(`/api/communications/booking/${booking.id}`);
      return await response.json();
    },
    enabled: isOpen && !!booking?.id && activeView === 'communications'
  });

  // Also fetch by client email to get all communications with this client
  const { data: clientCommunications = [] } = useQuery({
    queryKey: ['communications', 'client', booking?.clientEmail],
    queryFn: async () => {
      if (!booking?.clientEmail) return [];
      const response = await apiRequest(`/api/communications/client/${encodeURIComponent(booking.clientEmail)}`);
      return await response.json();
    },
    enabled: isOpen && !!booking?.clientEmail && activeView === 'communications'
  });

  // Combine and deduplicate communications
  const allCommunications = React.useMemo(() => {
    const combined = [...communications, ...clientCommunications];
    const uniqueIds = new Set();
    return combined.filter(comm => {
      if (uniqueIds.has(comm.id)) return false;
      uniqueIds.add(comm.id);
      return true;
    }).sort((a, b) => new Date(b.sentAt).getTime() - new Date(a.sentAt).getTime());
  }, [communications, clientCommunications]);

  // Send reply mutation
  const sendReplyMutation = useMutation({
    mutationFn: async (messageBody: string) => {
      const response = await apiRequest('/api/communications', {
        method: 'POST',
        body: JSON.stringify({
          bookingId: booking.id,
          clientName: booking.clientName,
          clientEmail: booking.clientEmail,
          communicationType: 'email',
          direction: 'outbound',
          subject: `Re: Booking for ${booking.eventDate ? format(new Date(booking.eventDate), 'MMMM d, yyyy') : 'your event'}`,
          messageBody,
          templateCategory: 'manual_reply'
        }),
      });
      return response.json();
    },
    onSuccess: () => {
      toast({
        title: "Reply sent",
        description: "Your message has been sent to the client.",
      });
      setReplyMessage('');
      setActiveView('communications');
      queryClient.invalidateQueries({ queryKey: ['communications'] });
    },
    onError: () => {
      toast({
        title: "Failed to send reply",
        description: "Please try again or contact support.",
        variant: "destructive",
      });
    }
  });

  const handleAction = (action: string) => {
    switch (action) {
      case 'view-details':
        navigate(`/bookings/edit/${booking.id}`);
        onClose();
        break;
      case 'edit-details':
        navigate(`/bookings/edit/${booking.id}?edit=true`);
        onClose();
        break;
      case 'view-communications':
        setActiveView('communications');
        break;
      case 'send-reply':
        setActiveView('reply');
        break;
      case 'send-invoice':
        navigate(`/invoices/new?bookingId=${booking.id}`);
        onClose();
        break;
      case 'send-contract':
        navigate(`/contracts/new?bookingId=${booking.id}`);
        onClose();
        break;
      default:
        break;
    }
  };

  const renderActions = () => (
    <div className="space-y-4">
      <div className="grid grid-cols-1 gap-3">
        <Button
