import { useState } from "react";
import { useMutation, useQueryClient, useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Upload, CheckCircle, AlertCircle, Calendar, RefreshCw } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import GoogleCalendarIntegration from "./google-calendar-integration";
import { useDialogLuminanceAware } from "@/hooks/use-luminance-aware";

interface CalendarImportProps {
  onImportComplete?: () => void;
}

export default function CalendarImport({ onImportComplete }: CalendarImportProps) {
  // Check Google Calendar status to show appropriate tab
  const { data: calendarStatus } = useQuery({
    queryKey: ['/api/google-calendar/status'],
    queryFn: async () => {
      const response = await apiRequest('/api/google-calendar/status');
      return response.json();
    },
  });
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [importStep, setImportStep] = useState<'select' | 'importing' | 'complete'>('select');
  const [importResult, setImportResult] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Apply global luminance-aware styling to dialogs
  useDialogLuminanceAware();

  // Local Calendar File Import
  const fileImportMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('icsFile', file);

      const response = await apiRequest('/api/calendar/import', {
        method: 'POST',
        body: formData,
      });
      return response.json();
    },
    onSuccess: (result) => {
      setImportResult(result);
      setImportStep('complete');
      queryClient.invalidateQueries({ queryKey: ['/api/bookings'] });
      onImportComplete?.();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to import calendar file",
        variant: "destructive",
      });
      setImportStep('select');
    },
  });

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file && file.name.endsWith('.ics')) {
      setSelectedFile(file);
    } else {
      toast({
        title: "Invalid File",
        description: "Please select a valid .ics calendar file",
        variant: "destructive",
      });
    }
  };

  const executeFileImport = () => {
    if (!selectedFile) {
      toast({
        title: "Missing File",
        description: "Please select an .ics file to import",
        variant: "destructive",
      });
      return;
    }

    setImportStep('importing');
    fileImportMutation.mutate(selectedFile);
  };

  const resetDialog = () => {
    setImportStep('select');
    setSelectedFile(null);
    setImportResult(null);
  };

  const handleDialogClose = () => {
    setIsDialogOpen(false);
    resetDialog();
  };

  return (
    <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm">
          <Calendar className="w-4 h-4 mr-2" />
          Calendar Sync
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[700px] max-h-[85vh] overflow-y-auto calendar-integration-dialog p-4">
        <DialogHeader>
          <DialogTitle>Calendar Integration</DialogTitle>
          <DialogDescription>
            Sync your bookings with your calendar using Google Calendar integration or import from .ics files
          </DialogDescription>
        </DialogHeader>

        <Tabs defaultValue={calendarStatus?.connected ? "google" : "import"} className="w-full">
          <TabsList className="grid w-full grid-cols-2">
            <TabsTrigger value="google" className="flex items-center gap-2">
              <Calendar className="w-4 h-4" />
              Google Calendar
            </TabsTrigger>
            <TabsTrigger value="import" className="flex items-center gap-2">
              <Upload className="w-4 h-4" />
              Import File
            </TabsTrigger>
          </TabsList>

          <TabsContent value="google" className="mt-6">
            <GoogleCalendarIntegration />
          </TabsContent>

          <TabsContent value="import" className="mt-6">
            <div className="text-center mb-6">
              <h3 className="text-lg font-medium mb-2">Import Calendar File (.ics)</h3>
              <p className="text-muted-foreground">
                Import your existing bookings from Apple Calendar, Outlook, or other calendar apps
              </p>
            </div>

            {importStep === 'select' && (
              <div className="space-y-4">
                <div>
              <Label htmlFor="file-upload">Calendar File (.ics)</Label>
              <Input
                id="file-upload"
                type="file"
                accept=".ics"
                onChange={handleFileUpload}
                className="mt-2"
              />
              {selectedFile && (
                <p className="text-sm text-muted-foreground mt-2">
                  Selected: {selectedFile.name}
                </p>
              )}
            </div>

            <div className="bg-muted p-4 rounded-lg">
              <h4 className="font-medium mb-2">How to export your calendar:</h4>
              <ul className="text-sm space-y-1 text-muted-foreground">
                <li>‚Ä¢ <strong>Google Calendar:</strong> Settings ‚Üí Import & Export ‚Üí Export</li>
                <li>‚Ä¢ <strong>Apple Calendar:</strong> File ‚Üí Export ‚Üí Export as .ics</li>
                <li>‚Ä¢ <strong>Outlook:</strong> File ‚Üí Save Calendar ‚Üí iCalendar format</li>
              </ul>
            </div>

                <div className="flex justify-end">
                  <Button onClick={executeFileImport} disabled={!selectedFile}>
                    Import Events
                  </Button>
                </div>
              </div>
            )}

            {importStep === 'importing' && (
              <div className="text-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
                <p className="text-muted-foreground">Importing calendar events...</p>
              </div>
            )}

            {importStep === 'complete' && importResult && (
              <div className="space-y-4">
                <div className="flex items-center text-green-600">
                  <CheckCircle className="w-5 h-5 mr-2" />
                  <span className="font-medium">Import Complete!</span>
                </div>

                <div className="bg-muted p-4 rounded-lg space-y-2">
                  <p><strong>Created:</strong> {importResult.created} new bookings</p>
                  <p><strong>Skipped:</strong> {importResult.skipped} duplicate events</p>
                  {importResult.errors && importResult.errors.length > 0 && (
                    <div className="flex items-start text-amber-600">
                      <AlertCircle className="w-4 h-4 mr-2 mt-0.5 flex-shrink-0" />
                      <div>
                        <p className="font-medium">Warnings:</p>
                        <ul className="text-sm mt-1">
                          {importResult.errors.slice(0, 3).map((error: string, index: number) => (
                            <li key={index}>‚Ä¢ {error}</li>
                          ))}
                        </ul>
                      </div>
                    </div>
                  )}
                </div>

                <div className="flex justify-between">
                  <Button variant="outline" onClick={handleDialogClose}>
                    Close
                  </Button>
                  <Button onClick={() => window.location.reload()}>
                    Refresh Calendar
                  </Button>
                </div>
              </div>
            )}
          </TabsContent>
        </Tabs>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/calendar-widget.ts.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { ArrowRight } from "lucide-react";
import { Link } from "wouter";
import type { Booking } from "@shared/schema";
import { getDisplayStatus, mapOldStatusToStage } from "@/utils/workflow-system";

export default function CalendarWidget() {
  const { data: allBookings = [], isLoading: bookingsLoading } = useQuery({
    queryKey: ["/api/bookings"],
  });

  // Filter for upcoming bookings within the next two weeks including today (no limit for dynamic sizing)
  const bookings = (allBookings as any[]).filter((booking: Booking) => {
    if (!booking.eventDate) return false;
    const eventDate = new Date(booking.eventDate);
    const today = new Date();
    const twoWeeksFromNow = new Date();
    today.setHours(0, 0, 0, 0); // Start of today to include today's gigs
    twoWeeksFromNow.setHours(0, 0, 0, 0);
    twoWeeksFromNow.setDate(today.getDate() + 14);
    return eventDate >= today && eventDate <= twoWeeksFromNow;
  }); // No limit - dynamic sizing

  const isLoading = bookingsLoading;

  const formatDate = (dateString: string) => {
    // Parse the date string as a local date to avoid timezone shifts
    const date = new Date(dateString);
    // If the date string is in ISO format (YYYY-MM-DD), parse it manually to avoid timezone issues
    if (dateString.includes('T') || dateString.includes('Z')) {
      // It's an ISO timestamp, extract just the date part
      const datePart = dateString.split('T')[0];
      const [year, month, day] = datePart.split('-').map(Number);
      const localDate = new Date(year, month - 1, day); // month is 0-indexed
      return {
        month: localDate.toLocaleDateString("en-GB", { month: "short" }).toUpperCase(),
        day: localDate.getDate().toString(),
      };
    }

    // For regular date strings, use normal parsing
    return {
      month: date.toLocaleDateString("en-GB", { month: "short" }).toUpperCase(),
      day: date.getDate().toString(),
    };
  };

  const getStatusColor = (status: string) => {
    const stage = mapOldStatusToStage(status);
    switch (stage) {
      case 'new': return "bg-amber-50 text-amber-900 border-amber-200";
      case 'in_progress': return "bg-orange-50 text-orange-900 border-orange-200"; 
      case 'client_confirms': return "bg-blue-50 text-blue-900 border-blue-200";
      case 'confirmed': return "bg-green-50 text-green-900 border-green-200";
      case 'rejected': return "bg-red-50 text-red-900 border-red-200";
      case 'completed': return "bg-green-50 text-green-900 border-green-200";
      default: return "bg-gray-50 text-gray-900 border-gray-200";
    }
  };

  const getStatusBadgeColor = (status: string) => {
    const stage = mapOldStatusToStage(status);
    switch (stage) {
      case 'new': return "text-amber-600";
      case 'in_progress': return "text-orange-600";
      case 'client_confirms': return "text-blue-600";
      case 'confirmed': return "text-green-600";
      case 'rejected': return "text-red-600";
      case 'completed': return "text-green-600";
      default: return "text-gray-600";
    }
  };

  // Get upcoming bookings (no need to combine with enquiries as they're now in the same table)
  const getUpcomingGigs = () => {
    const today = new Date();
    today.setHours(0, 0, 0, 0); // Start of today

    // Filter and sort upcoming bookings including today's gigs (no limit for dynamic sizing)
    return bookings
      .filter((booking: Booking) => booking.eventDate && new Date(booking.eventDate) >= today)
      .sort((a: any, b: any) => new Date(a.eventDate).getTime() - new Date(b.eventDate).getTime())
      .map((booking: Booking) => ({
        id: booking.id,
        title: booking.title,
        clientName: booking.clientName,
        eventDate: booking.eventDate,
        eventTime: booking.eventTime,
        venue: booking.venue,
        fee: booking.fee,
        status: booking.status,
        type: 'booking'
      }));
  };

  const upcomingGigs = getUpcomingGigs();

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Upcoming Gigs</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="animate-pulse space-y-4">
            {[...Array(3)].map((_, i) => (
              <div key={i} className="flex items-center space-x-3 p-3 rounded-lg">
                <div className="w-12 h-12 bg-gray-200 rounded"></div>
                <div className="flex-1 space-y-2">
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                  <div className="h-3 bg-gray-200 rounded w-1/2"></div>
                </div>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card className="shadow-sm">
      <CardHeader className="pb-6">
        <div className="flex items-center justify-between">
          <CardTitle className="text-xl font-semibold">Upcoming Gigs</CardTitle>
          <Link href="/bookings">
            <Button variant="outline" size="sm" className="h-9">
              <ArrowRight className="w-4 h-4 mr-2" />
              View Calendar
            </Button>
          </Link>
        </div>
      </CardHeader>
      <CardContent>
        {/* Single column layout with wider, enlarged cards */}
        <div className="space-y-4">
          {upcomingGigs.map((gig: any) => {
            const dateInfo = formatDate(gig.eventDate);
            return (
              <div 
                key={gig.id}
                className={`flex items-center gap-3 p-4 sm:p-6 rounded-lg ${getStatusColor(gig.status)} hover:shadow-lg transition-all duration-200 cursor-pointer border-l-4 border-current`}
                onClick={() => {
                  // Single click - go to calendar with highlight
                  window.location.href = `/bookings?view=calendar&highlight=${gig.id}`;
                }}
                onDoubleClick={() => {
                  // Double click - go to booking form
                  window.location.href = `/new-booking?edit=${gig.id}`;
                }}
              >
                  {/* Responsive date box */}
                  <div className="text-center flex-shrink-0 w-16 sm:w-20">
                    <div className={`text-xs sm:text-sm font-semibold ${getStatusBadgeColor(gig.status)} uppercase tracking-wide`}>
                      {dateInfo.month}
                    </div>
                    <div className="text-xl sm:text-3xl font-bold text-gray-900">
                      {dateInfo.day}
                    </div>
                  </div>

                  {/* Content with proper overflow handling */}
                  <div className="flex-1 min-w-0">
                    <h4 className="text-base sm:text-lg font-semibold text-gray-900 truncate">{gig.title}</h4>
                    <p className="text-sm sm:text-base text-gray-700 mt-1 truncate">{gig.venue}</p>
                    {gig.eventTime && (
                      <p className="text-xs sm:text-sm text-gray-600 mt-1">Time: {gig.eventTime}</p>
                    )}
                    <div className="flex items-center gap-2 sm:gap-3 mt-2 flex-wrap">
                      <span className={`text-xs sm:text-sm font-medium px-2 sm:px-3 py-1 rounded-full bg-white/50 ${getStatusBadgeColor(gig.status)}`}>
                        {getDisplayStatus(gig.status)}
                      </span>
                      {gig.fee && (
                        <span className="text-xs sm:text-sm font-semibold text-gray-900">
                          ¬£{gig.fee}
                        </span>
                      )}
                    </div>
                  </div>
                </div>
            );
          })}
        </div>

        {upcomingGigs.length === 0 && (
          <div className="text-center py-12 text-gray-500">
            <p className="text-lg">No upcoming gigs</p>
            <p className="text-base mt-2">New bookings will appear here</p>
          </div>
        )}
      </CardContent>
    </Card>
  );
}

