        )}

        <Link href="/calendar">
          <Button variant="ghost" className="w-full justify-center">
            View Full Calendar <ArrowRight className="w-4 h-4 ml-1" />
          </Button>
        </Link>
      </CardContent>
    </Card>
  );
}

