import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { useQuery } from "@tanstack/react-query";
import { CheckCircle, FileText, Eye, Download, Calendar } from "lucide-react";

interface Contract {
  id: number;
  contractNumber: string;
  clientName: string;
  clientEmail: string;
  eventDate: string;
  eventTime: string;
  venue: string;
  fee: string;
  status: string;
  signedAt?: string;
}

export default function RecentSignedContracts() {
  const { data: contracts = [], isLoading } = useQuery<Contract[]>({
    queryKey: ["/api/contracts"],
  });

  // Filter for signed contracts and sort by signed date (most recent first)
  const signedContracts = contracts
    .filter(contract => contract.status === 'signed')
    .sort((a, b) => {
      if (!a.signedAt || !b.signedAt) return 0;
      return new Date(b.signedAt).getTime() - new Date(a.signedAt).getTime();
    })
    .slice(0, 3); // Show only the 3 most recent

  const handleViewContract = (contract: Contract) => {
    window.open(`/view-contract/${contract.id}`, '_blank');
  };

  const handleDownloadContract = async (contract: Contract) => {
    try {
      const response = await fetch(`/api/contracts/${contract.id}/pdf`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `Contract-${contract.contractNumber}-Signed.pdf`;
      a.click();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Error downloading contract:', error);
      alert('Failed to download contract. Please try again.');
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2 text-sm md:text-base">
            <CheckCircle className="w-4 h-4 text-green-600" />
            Recent Signed Contracts
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="animate-pulse space-y-3">
            <div className="h-4 bg-gray-200 rounded"></div>
            <div className="h-4 bg-gray-200 rounded"></div>
            <div className="h-4 bg-gray-200 rounded"></div>
          </div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2 text-sm md:text-base">
          <CheckCircle className="w-4 h-4 text-green-600" />
          Recent Signed Contracts
        </CardTitle>
      </CardHeader>
      <CardContent>
        {signedContracts.length === 0 ? (
          <div className="text-center py-4">
            <FileText className="w-8 h-8 text-gray-400 mx-auto mb-2" />
            <p className="text-sm text-gray-500">No signed contracts yet</p>
            <p className="text-xs text-gray-400 mt-1">Signed contracts will appear here</p>
          </div>
        ) : (
          <div className="space-y-3">
            {signedContracts.map((contract) => (
              <div key={contract.id} className="border rounded-lg p-3 bg-green-50">
                <div className="flex items-start justify-between">
                  <div className="flex-1 min-w-0">
                    <div className="flex items-center gap-2 mb-1">
                      <Badge variant="default" className="bg-green-600 text-white text-xs">
                        <CheckCircle className="w-3 h-3 mr-1" />
                        Signed
                      </Badge>
                      <span className="text-xs font-medium text-green-700">
                        {contract.contractNumber}
                      </span>
                    </div>

                    <h4 className="font-medium text-sm text-gray-900 truncate">
                      {contract.clientName}
                    </h4>

                    <div className="flex items-center gap-3 mt-1 text-xs text-gray-600">
                      <span className="flex items-center gap-1">
                        <Calendar className="w-3 h-3" />
                        {new Date(contract.eventDate).toLocaleDateString('en-GB')}
                      </span>
                      <span className="truncate">
                        {contract.venue}
                      </span>
                    </div>

                    <div className="text-xs text-green-600 mt-1">
                      Signed: {contract.signedAt ? new Date(contract.signedAt).toLocaleDateString('en-GB') : 'Recently'}
                    </div>
                  </div>

                  <div className="flex flex-col gap-1 ml-2">
                    <Button
                      size="sm"
                      variant="outline"
                      className="text-xs h-6 px-2"
                      onClick={() => handleViewContract(contract)}
                    >
                      <Eye className="w-3 h-3 mr-1" />
                      View
                    </Button>
                    <Button
                      size="sm"
                      variant="outline"
                      className="text-xs h-6 px-2"
                      onClick={() => handleDownloadContract(contract)}
                    >
                      <Download className="w-3 h-3 mr-1" />
                      PDF
                    </Button>
                  </div>
                </div>
              </div>
            ))}

            {signedContracts.length > 0 && (
              <div className="text-center pt-2">
                <Button
                  variant="ghost"
                  size="sm"
                  className="text-xs text-blue-600 hover:text-blue-700"
                  onClick={() => window.location.href = '/contracts?status=signed'}
                >
                  View all signed contracts ‚Üí
                </Button>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/calendar-import.ts.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Upload, CheckCircle, AlertCircle } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";

interface CalendarImportProps {
  onImportComplete?: () => void;
}

export default function CalendarImport({ onImportComplete }: CalendarImportProps) {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [importStep, setImportStep] = useState<'select' | 'importing' | 'complete'>('select');
  const [importResult, setImportResult] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Local Calendar File Import
  const fileImportMutation = useMutation({
    mutationFn: async (file: File) => {
      const formData = new FormData();
      formData.append('icsFile', file);

      const response = await apiRequest('/api/calendar/import', {
        method: 'POST',
        body: formData,
      });
      return response.json();
    },
    onSuccess: (result) => {
      setImportResult(result);
      setImportStep('complete');
      queryClient.invalidateQueries({ queryKey: ['/api/bookings'] });
      queryClient.invalidateQueries({ queryKey: ['/api/bookings'] }); // Phase 3: Use main bookings table
      queryClient.invalidateQueries({ queryKey: ['/api/enquiries'] }); // Keep for backwards compatibility
      onImportComplete?.();
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to import calendar file",
        variant: "destructive",
      });
      setImportStep('select');
    },
  });

  const handleFileUpload = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file && file.name.endsWith('.ics')) {
      setSelectedFile(file);
    } else {
      toast({
        title: "Invalid File",
        description: "Please select a valid .ics calendar file",
        variant: "destructive",
      });
    }
  };

  const executeFileImport = () => {
    if (!selectedFile) {
      toast({
        title: "Missing File",
        description: "Please select an .ics file to import",
        variant: "destructive",
      });
      return;
    }

    setImportStep('importing');
    fileImportMutation.mutate(selectedFile);
  };

  const resetDialog = () => {
    setImportStep('select');
    setSelectedFile(null);
    setImportResult(null);
  };

  const handleDialogClose = () => {
    setIsDialogOpen(false);
    resetDialog();
  };

  return (
    <Dialog open={isDialogOpen} onOpenChange={setIsDialogOpen}>
      <DialogTrigger asChild>
        <Button variant="outline" size="sm">
          <Upload className="w-4 h-4 mr-2" />
          Import Calendar
        </Button>
      </DialogTrigger>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle>Import Calendar Events</DialogTitle>
          <DialogDescription>
            Import your existing bookings from a .ics calendar file (Google Calendar, Apple Calendar, Outlook, etc.)
          </DialogDescription>
        </DialogHeader>

        {importStep === 'select' && (
          <div className="space-y-4">
            <div>
              <Label htmlFor="file-upload">Calendar File (.ics)</Label>
              <Input
                id="file-upload"
                type="file"
                accept=".ics"
                onChange={handleFileUpload}
                className="mt-2"
              />
              {selectedFile && (
                <p className="text-sm text-muted-foreground mt-2">
                  Selected: {selectedFile.name}
                </p>
              )}
            </div>

            <div className="bg-muted p-4 rounded-lg">
              <h4 className="font-medium mb-2">How to export your calendar:</h4>
              <ul className="text-sm space-y-1 text-muted-foreground">
                <li>‚Ä¢ <strong>Google Calendar:</strong> Settings ‚Üí Import & Export ‚Üí Export</li>
                <li>‚Ä¢ <strong>Apple Calendar:</strong> File ‚Üí Export ‚Üí Export as .ics</li>
                <li>‚Ä¢ <strong>Outlook:</strong> File ‚Üí Save Calendar ‚Üí iCalendar format</li>
              </ul>
            </div>

            <div className="flex justify-end">
              <Button onClick={executeFileImport} disabled={!selectedFile}>
                Import Events
              </Button>
            </div>
          </div>
        )}

        {importStep === 'importing' && (
          <div className="text-center py-8">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600 mx-auto mb-4"></div>
            <p className="text-muted-foreground">Importing calendar events...</p>
          </div>
        )}

        {importStep === 'complete' && importResult && (
          <div className="space-y-4">
            <div className="flex items-center text-green-600">
              <CheckCircle className="w-5 h-5 mr-2" />
              <span className="font-medium">Import Complete!</span>
            </div>

            <div className="bg-muted p-4 rounded-lg space-y-2">
              <p><strong>Created:</strong> {importResult.created} new bookings</p>
              <p><strong>Skipped:</strong> {importResult.skipped} duplicate events</p>
              {importResult.errors && importResult.errors.length > 0 && (
                <div className="flex items-start text-amber-600">
                  <AlertCircle className="w-4 h-4 mr-2 mt-0.5 flex-shrink-0" />
                  <div>
                    <p className="font-medium">Warnings:</p>
                    <ul className="text-sm mt-1">
                      {importResult.errors.slice(0, 3).map((error: string, index: number) => (
                        <li key={index}>‚Ä¢ {error}</li>
                      ))}
                    </ul>
                  </div>
                </div>
              )}
            </div>

            <div className="flex justify-between">
              <Button variant="outline" onClick={handleDialogClose}>
                Close
              </Button>
              <Button onClick={() => window.location.reload()}>
                Refresh Calendar
              </Button>
            </div>
          </div>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/components/conflicts-widget.t.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { AlertTriangle, Calendar, Clock, MapPin, User, CheckCircle } from "lucide-react";
import { useState } from "react";
import { apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";

interface BookingConflict {
  id: number;
  enquiryId: number;
  conflictType: 'booking' | 'enquiry';
  conflictId: number;
  severity: 'high' | 'medium' | 'low';
  message: string;
  resolved: boolean;
  createdAt: string;
  enquiry?: {
    title: string;
    clientName: string;
    eventDate: string;
    eventTime?: string;
    venue?: string;
  };
  conflictItem?: {
    title: string;
    clientName: string;
    eventDate: string;
    eventTime?: string;
    venue?: string;
  };
}

export default function ConflictsWidget() {
  const [selectedConflict, setSelectedConflict] = useState<BookingConflict | null>(null);
  const { toast } = useToast();

  const { data: conflicts = [], isLoading } = useQuery({
    queryKey: ["/api/conflicts"],
    staleTime: 30000, // 30 seconds
    cacheTime: 5 * 60 * 1000, // 5 minutes
  });

  const unresolvedConflicts = conflicts.filter((c: BookingConflict) => !c.resolved);

  const resolveConflict = async (conflictId: number) => {
    try {
      await apiRequest(`/api/conflicts/${conflictId}/resolve`, {
        method: "POST",
        body: JSON.stringify({ resolution: "manual", notes: "Resolved by user" }),
      });

      toast({
        title: "Conflict resolved",
        description: "The scheduling conflict has been marked as resolved.",
      });

      // Refresh the conflicts list
      window.location.reload();
    } catch (error) {
      toast({
        title: "Error",
        description: "Failed to resolve conflict. Please try again.",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <AlertTriangle className="h-5 w-5" />
            Scheduling Conflicts
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-sm text-muted-foreground">Loading conflicts...</div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <AlertTriangle className="h-5 w-5" />
          Scheduling Conflicts
          {unresolvedConflicts.length > 0 && (
            <Badge variant="destructive">{unresolvedConflicts.length}</Badge>
          )}
        </CardTitle>
      </CardHeader>
      <CardContent>
        {unresolvedConflicts.length === 0 ? (
          <div className="text-sm text-muted-foreground flex items-center gap-2">
            <CheckCircle className="h-4 w-4 text-green-500" />
            No scheduling conflicts
          </div>
        ) : (
          <div className="space-y-3">
            {unresolvedConflicts.slice(0, 3).map((conflict: BookingConflict) => (
              <div
                key={conflict.id}
                className={`p-3 rounded-lg border ${
                  conflict.severity === 'high' ? 'border-red-200 bg-red-50' :
                  conflict.severity === 'medium' ? 'border-yellow-200 bg-yellow-50' :
                  'border-blue-200 bg-blue-50'
                }`}
              >
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-2 mb-1">
                      <Badge 
                        variant={conflict.severity === 'high' ? 'destructive' : 'default'}
                        className="text-xs"
                      >
                        {conflict.severity.toUpperCase()}
                      </Badge>
                      <span className="text-sm font-medium">
                        {conflict.enquiry?.clientName}
                      </span>
                    </div>
                    <p className="text-sm text-muted-foreground mb-2">
                      {conflict.message}
                    </p>
                    <div className="flex items-center gap-4 text-xs text-muted-foreground">
                      <div className="flex items-center gap-1">
                        <Calendar className="h-3 w-3" />
                        {conflict.enquiry?.eventDate ? 
                          new Date(conflict.enquiry.eventDate).toLocaleDateString() : 
                          'No date'
                        }
                      </div>
                      {conflict.enquiry?.eventTime && (
                        <div className="flex items-center gap-1">
                          <Clock className="h-3 w-3" />
                          {conflict.enquiry.eventTime}
                        </div>
                      )}
                      {conflict.enquiry?.venue && (
                        <div className="flex items-center gap-1">
                          <MapPin className="h-3 w-3" />
                          {conflict.enquiry.venue}
                        </div>
                      )}
                    </div>
                  </div>
                  <div className="flex gap-2">
                    <Dialog>
                      <DialogTrigger asChild>
                        <Button 
                          variant="outline" 
                          size="sm"
                          onClick={() => setSelectedConflict(conflict)}
                        >
                          View Details
                        </Button>
                      </DialogTrigger>
                      <DialogContent className="max-w-md">
                        <DialogHeader>
                          <DialogTitle>Scheduling Conflict Details</DialogTitle>
                        </DialogHeader>
                        {selectedConflict && (
                          <div className="space-y-4">
                            <div className="p-3 bg-gray-50 rounded-lg">
                              <h4 className="font-medium mb-2">New Enquiry</h4>
                              <div className="space-y-1 text-sm">
                                <div className="flex items-center gap-2">
                                  <User className="h-4 w-4" />
                                  {selectedConflict.enquiry?.clientName}
                                </div>
                                <div className="flex items-center gap-2">
                                  <Calendar className="h-4 w-4" />
                                  {selectedConflict.enquiry?.eventDate ? 
                                    new Date(selectedConflict.enquiry.eventDate).toLocaleDateString() : 
                                    'No date'
                                  }
                                </div>
                                {selectedConflict.enquiry?.eventTime && (
                                  <div className="flex items-center gap-2">
                                    <Clock className="h-4 w-4" />
                                    {selectedConflict.enquiry.eventTime}
                                  </div>
                                )}
                                {selectedConflict.enquiry?.venue && (
                                  <div className="flex items-center gap-2">
                                    <MapPin className="h-4 w-4" />
                                    {selectedConflict.enquiry.venue}
                                  </div>
                                )}
                              </div>
                            </div>

                            <div className="p-3 bg-red-50 rounded-lg">
                              <h4 className="font-medium mb-2">Conflicts With</h4>
                              <div className="space-y-1 text-sm">
                                <div className="flex items-center gap-2">
                                  <User className="h-4 w-4" />
                                  {selectedConflict.conflictItem?.clientName}
                                </div>
                                <div className="flex items-center gap-2">
                                  <Calendar className="h-4 w-4" />
                                  {selectedConflict.conflictItem?.eventDate ? 
                                    new Date(selectedConflict.conflictItem.eventDate).toLocaleDateString() : 
                                    'No date'
                                  }
                                </div>
                                {selectedConflict.conflictItem?.eventTime && (
                                  <div className="flex items-center gap-2">
                                    <Clock className="h-4 w-4" />
                                    {selectedConflict.conflictItem.eventTime}
                                  </div>
                                )}
                                {selectedConflict.conflictItem?.venue && (
                                  <div className="flex items-center gap-2">
                                    <MapPin className="h-4 w-4" />
                                    {selectedConflict.conflictItem.venue}
                                  </div>
                                )}
                              </div>
                            </div>

                            <div className="p-3 bg-yellow-50 rounded-lg">
                              <h4 className="font-medium mb-2">Analysis</h4>
                              <p className="text-sm">{selectedConflict.message}</p>
                            </div>

                            <div className="flex justify-end gap-2">
                              <Button 
                                variant="outline"
                                onClick={() => setSelectedConflict(null)}
                              >
                                Close
                              </Button>
                              <Button 
                                onClick={() => resolveConflict(selectedConflict.id)}
                                className="bg-green-600 hover:bg-green-700"
                              >
                                Mark as Resolved
                              </Button>
                            </div>
                          </div>
                        )}
                      </DialogContent>
                    </Dialog>
                  </div>
                </div>
              </div>
            ))}

            {unresolvedConflicts.length > 3 && (
              <div className="text-center">
                <Button variant="outline" size="sm">
                  View All {unresolvedConflicts.length} Conflicts
                </Button>
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

client/src/components/notifications-drop.content:
import { useState, useEffect } from "react";
import { Bell, AlertTriangle, Calendar, DollarSign, FileText, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";

interface Notification {
  id: number;
  type: 'overdue_invoice' | 'contract_expiry' | 'compliance_alert' | 'new_enquiry';
  title: string;
  message: string;
  timestamp: Date;
  isRead: boolean;
  actionUrl?: string;
  priority: 'high' | 'medium' | 'low';
}

export default function NotificationsDropdown() {
  const [isOpen, setIsOpen] = useState(false);
  const [notifications, setNotifications] = useState<Notification[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchNotifications();
  }, []);

  const fetchNotifications = async () => {
    try {
      setLoading(true);

      // Fetch overdue invoices
      const invoicesResponse = await fetch('/api/invoices', {
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' }
      });

      const invoices = await invoicesResponse.json();
      const overdueInvoices = invoices.filter((invoice: any) => 
        invoice.status === 'overdue' || 
        (invoice.status === 'sent' && new Date(invoice.dueDate) < new Date())
      );

      // Phase 2: Fetch recent enquiries from new bookings table
      const enquiriesResponse = await fetch('/api/bookings', {
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' }
      });

      const enquiries = await enquiriesResponse.json();
      const recentEnquiries = enquiries.filter((enquiry: any) => 
        enquiry.status === 'new' && 
        new Date(enquiry.createdAt) > new Date(Date.now() - 24 * 60 * 60 * 1000)
      );

      // Create notifications
      const notificationsList: Notification[] = [
        ...overdueInvoices.map((invoice: any, index: number) => ({
          id: index + 1,
          type: 'overdue_invoice' as const,
          title: 'Overdue Invoice',
          message: `Invoice ${invoice.invoiceNumber} is overdue (${invoice.clientName})`,
          timestamp: new Date(invoice.dueDate),
          isRead: false,
          actionUrl: `/invoices`,
          priority: 'high' as const
        })),
        ...recentEnquiries.slice(0, 3).map((enquiry: any, index: number) => ({
          id: overdueInvoices.length + index + 1,
          type: 'new_enquiry' as const,
          title: 'New Enquiry',
          message: `New enquiry from ${enquiry.clientName}`,
          timestamp: new Date(enquiry.createdAt),
          isRead: false,
          actionUrl: `/enquiries`,
          priority: 'medium' as const
        }))
      ];

      setNotifications(notificationsList);
    } catch (error) {
      console.error('Error fetching notifications:', error);
      setNotifications([]);
    } finally {
      setLoading(false);
    }
  };

  const unreadCount = notifications.filter(n => !n.isRead).length;

  const getIcon = (type: Notification['type']) => {
    switch (type) {
      case 'overdue_invoice':
        return <DollarSign className="w-4 h-4 text-red-500" />;
      case 'contract_expiry':
        return <FileText className="w-4 h-4 text-orange-500" />;
      case 'compliance_alert':
        return <AlertTriangle className="w-4 h-4 text-yellow-500" />;
      case 'new_enquiry':
        return <Calendar className="w-4 h-4 text-blue-500" />;
      default:
        return <Bell className="w-4 h-4 text-gray-500" />;
    }
  };

  const getPriorityColor = (priority: Notification['priority']) => {
    switch (priority) {
      case 'high':
        return 'bg-red-100 text-red-800';
      case 'medium':
        return 'bg-yellow-100 text-yellow-800';
      case 'low':
        return 'bg-green-100 text-green-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const markAsRead = (id: number) => {
    setNotifications(prev => 
      prev.map(n => n.id === id ? { ...n, isRead: true } : n)
    );
  };

  const markAllAsRead = () => {
    setNotifications(prev => prev.map(n => ({ ...n, isRead: true })));
  };

  const formatTimestamp = (date: Date) => {
    const now = new Date();
    const diff = now.getTime() - date.getTime();
    const minutes = Math.floor(diff / (1000 * 60));
    const hours = Math.floor(diff / (1000 * 60 * 60));
    const days = Math.floor(diff / (1000 * 60 * 60 * 24));

    if (minutes < 60) {
      return `${minutes}m ago`;
    } else if (hours < 24) {
      return `${hours}h ago`;
    } else {
      return `${days}d ago`;
    }
  };

  const handleNotificationClick = (notification: Notification) => {
    markAsRead(notification.id);
    if (notification.actionUrl) {
      window.location.href = notification.actionUrl;
    }
    setIsOpen(false);
  };

  return (
    <div className="relative">
      <Button
        variant="ghost"
        size="icon"
        className="relative"
        onClick={() => setIsOpen(!isOpen)}
      >
        <Bell className="w-5 h-5" />
        {unreadCount > 0 && (
          <Badge
            variant="destructive"
            className="absolute -top-2 -right-2 h-5 w-5 flex items-center justify-center p-0 text-xs"
          >
            {unreadCount > 9 ? '9+' : unreadCount}
          </Badge>
        )}
      </Button>

      {isOpen && (
        <div className="absolute right-0 mt-2 w-80 z-50">
          <Card className="shadow-lg border">
            <CardHeader className="pb-3">
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg">Notifications</CardTitle>
                <div className="flex items-center space-x-2">
                  {unreadCount > 0 && (
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={markAllAsRead}
                      className="text-xs"
                    >
                      Mark all read
                    </Button>
                  )}
                  <Button
                    variant="ghost"
                    size="icon"
                    onClick={() => setIsOpen(false)}
                    className="h-6 w-6"
                  >
                    <X className="w-4 h-4" />
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent className="p-0">
              <div className="max-h-96 overflow-y-auto">
                {loading ? (
                  <div className="p-4 text-center text-gray-500">
                    Loading notifications...
                  </div>
                ) : notifications.length === 0 ? (
                  <div className="p-4 text-center text-gray-500">
                    <Bell className="w-8 h-8 mx-auto mb-2 text-gray-300" />
                    <p>No notifications</p>
                  </div>
                ) : (
                  <div className="divide-y">
                    {notifications.map((notification) => (
                      <div
                        key={notification.id}
                        className={`p-4 hover:bg-gray-50 cursor-pointer transition-colors ${
                          !notification.isRead ? 'bg-blue-50' : ''
                        }`}
                        onClick={() => handleNotificationClick(notification)}
                      >
                        <div className="flex items-start space-x-3">
                          <div className="flex-shrink-0 mt-1">
                            {getIcon(notification.type)}
                          </div>
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between">
                              <p className="text-sm font-medium text-gray-900">
                                {notification.title}
                              </p>
                              <Badge 
                                variant="secondary" 
                                className={`${getPriorityColor(notification.priority)} text-xs`}
                              >
                                {notification.priority}
                              </Badge>
                            </div>
                            <p className="text-sm text-gray-600 mt-1">
                              {notification.message}
                            </p>
                            <p className="text-xs text-gray-500 mt-1">
                              {formatTimestamp(notification.timestamp)}
                            </p>
                          </div>
                          {!notification.isRead && (
                            <div className="flex-shrink-0">
                              <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                            </div>
                          )}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
              {notifications.length > 0 && (
                <>
                  <Separator />
                  <div className="p-3 bg-gray-50">
                    <Button 
                      variant="ghost" 
                      className="w-full text-sm text-center"
                      onClick={() => {
                        setIsOpen(false);
                        window.location.href = '/dashboard';
                      }}
                    >
                      View all notifications
                    </Button>
                  </div>
                </>
              )}
            </CardContent>
          </Card>
        </div>
      )}
    </div>
  );
}

client/src/components/theme-provider.tsx.content:
import { createContext, useContext, useEffect, useState } from 'react';

type Theme = 'light' | 'dark';

interface ThemeProviderProps {
  children: React.ReactNode;
  defaultTheme?: Theme;
}

interface ThemeContextType {
  theme: Theme;
  setTheme: (theme: Theme) => void;
}

const ThemeContext = createContext<ThemeContextType | undefined>(undefined);

export function ThemeProvider({ children, defaultTheme = 'light' }: ThemeProviderProps) {
  const [theme, setTheme] = useState<Theme>(defaultTheme);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme') as Theme;
    if (savedTheme) {
      setTheme(savedTheme);
    }
  }, []);

  useEffect(() => {
    const root = document.documentElement;
    root.classList.remove('light', 'dark');
    root.classList.add(theme);
    localStorage.setItem('theme', theme);
  }, [theme]);

  return (
    <ThemeContext.Provider value={{ theme, setTheme }}>
      {children}
    </ThemeContext.Provider>
  );
}

export function useTheme() {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
}

