import FormData from 'form-data';
import Mailgun from 'mailgun.js';

const mailgun = new Mailgun(FormData);

if (!process.env.MAILGUN_API_KEY) {
  throw new Error("MAILGUN_API_KEY environment variable must be set");
}

if (!process.env.MAILGUN_DOMAIN) {
  throw new Error("MAILGUN_DOMAIN environment variable must be set");
}

const mg = mailgun.client({
  username: 'api',
  key: process.env.MAILGUN_API_KEY,
  url: 'https://api.mailgun.net' // Use US region
});

interface EmailAttachment {
  content: string; // Base64 encoded content
  filename: string;
  type: string;
}

export interface MailgunEmailParams {
  to: string;
  from: string;
  subject: string;
  text?: string;
  html?: string;
  attachments?: EmailAttachment[];
}

export async function sendEmail(params: MailgunEmailParams): Promise<boolean> {
  try {
    const emailData: any = {
      from: params.from,
      to: params.to,
      subject: params.subject,
      text: params.text,
      html: params.html,
    };

    // Add attachments if present
    if (params.attachments && params.attachments.length > 0) {
      emailData.attachment = params.attachments.map(att => ({
        data: Buffer.from(att.content, 'base64'),
        filename: att.filename,
        contentType: att.type
      }));
    }

    const response = await mg.messages.create(process.env.MAILGUN_DOMAIN!, emailData);

    console.log('Mailgun email sent successfully:', response.id);
    return true;
  } catch (error) {
    console.error('Mailgun email error:', error);
    return false;
  }
}

