      if (error.message && error.message.includes('toISOString')) {
        console.error('üö® ‚ö†Ô∏è FOUND THE toISOString ERROR IN PRIORITY ROUTE!');
        console.error('üö® This confirms the error is in the webhook handler itself');
      }

      if (!res.headersSent) {
        res.status(500).json({ 
          error: 'Priority webhook processing failed',
