 */

import pkg from 'pg';
const { Pool } = pkg;

async function debugContractSigningConfirmationEmails() {
  console.log('üîç DEBUGGING CONTRACT SIGNING CONFIRMATION EMAILS');
  console.log('=' .repeat(60));

  const pool = new Pool({
