            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <FormField
              control={form.control}
              name="venue"
              render={({ field }) => (
                <FormItem className="space-y-2">
                  <FormLabel>Venue</FormLabel>
                  <FormControl>
                    <Input placeholder="The Grand Hotel, London" {...field} value={field.value || ""} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="eventDate"
              render={({ field }) => (
                <FormItem className="space-y-2">
                  <FormLabel>Event Date</FormLabel>
                  <FormControl>
                    <Input type="date" {...field} value={field.value || ""} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
            <FormField
              control={form.control}
              name="eventTime"
              render={({ field }) => (
                <FormItem className="space-y-2">
                  <FormLabel>Start Time</FormLabel>
                  <FormControl>
                    <Input placeholder="7:00 PM" {...field} value={field.value || ""} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="eventEndTime"
              render={({ field }) => (
                <FormItem className="space-y-2">
                  <FormLabel>Finish Time</FormLabel>
                  <FormControl>
                    <Input placeholder="11:00 PM" {...field} value={field.value || ""} />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />
            <FormField
              control={form.control}
              name="fee"
              render={({ field }) => (
                <FormItem className="space-y-2">
                  <FormLabel>Performance Fee (¬£)</FormLabel>
                  <FormControl>
                    <Input type="number" placeholder="1500" {...field} value={field.value || ""} />
